import groovy.json.JsonSlurper
import groovy.util.XmlParser
import org.custommonkey.xmlunit.DetailedDiff
import java.util.List 

XMLUnit.setIgnoreAttributeOrder(true)
XMLUnit.setIgnoreWhitespace(true)
XMLUnit.setIgnoreComments(true)
XMLUnit.setIgnoreDiffBetweenTextAndCDATA(true)
XMLUnit.setNormalizeWhitespace(true)

def slurper = new JsonSlurper()
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def expectedTriples = context.expand('${getExpectedLegalEntityTriples#Response}')
def actualTriples = context.expand('${getActualLegalEntityTriples#Response}')
log.info(expectedTriples)
log.info(actualTriples)
DetailedDiff myDiff = new DetailedDiff(new Diff(expectedTriples, actualTriples));
List allDifferences = myDiff.getAllDifferences();
log.info (allDifferences)
assert(allDifferences.size() == 0)

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def response = context.expand( '${Post-Country-UK#Response}' )
testRunner.testCase.testSuite.setPropertyValue("country-uk-id", response);
log.info(response)

